mapper.update:
UPDATE ${tablePrefix}job_info j
SET
`VERSION` = #{version, jdbcType=INTEGER},
UPDATE_TIME = #{updateTime, jdbcType=TIMESTAMP},
DEPLOYMENT_TIME = #{deploymentTime, jdbcType=TIMESTAMP}



    and REVERSION = #{reversion, jdbcType=INTEGER}

mapper.select:
SELECT ji.`JOB_NAME`, ji.`DISPLAY_NAME`, ji.`JOB_CONTENT`, ji.`UPDATE_TIME`, ji.`DEPLOYMENT_TIME`, ji.`TENANT_ID`, j.MAX_REVERSION, ji.REVERSION, j.MAX_VERSION, ji.`VERSION`, j.`STATUS`, j.`CREATE_TIME`, j.REMARK from ${tablePrefix}job j join ${tablePrefix}job_info ji using(JOB_NAME, TENANT_ID)
and REVERSION = #{reversion, jdbcType=INTEGER}
order by REVERSION desc limit 1

SELECT * FROM ( SELECT ji.`JOB_NAME`, ji.`DISPLAY_NAME`, ji.`JOB_CONTENT`, ji.`UPDATE_TIME`, ji.`DEPLOYMENT_TIME`, ji.`TENANT_ID`, j.MAX_REVERSION, ji.REVERSION, j.MAX_VERSION, ji.`VERSION`, j.`STATUS`, j.`CREATE_TIME`, j.REMARK FROM ${tablePrefix}job j JOIN ${tablePrefix}job_info ji USING (JOB_NAME, TENANT_ID) JOIN (SELECT JOB_NAME, TENANT_ID, MAX(`VERSION`) AS `VERSION` FROM ${tablePrefix}job_info GROUP BY JOB_NAME, TENANT_ID) ji_m USING (JOB_NAME, TENANT_ID, `VERSION`) WHERE j.MAX_VERSION IS NOT NULL UNION SELECT ji.`JOB_NAME`, ji.`DISPLAY_NAME`, ji.`JOB_CONTENT`, ji.`UPDATE_TIME`, ji.`DEPLOYMENT_TIME`, ji.`TENANT_ID`, j.MAX_REVERSION, ji.REVERSION, j.MAX_VERSION, ji.`VERSION`, j.`STATUS`, j.`CREATE_TIME`, j.REMARK FROM ${tablePrefix}job j JOIN ${tablePrefix}job_info ji USING (JOB_NAME, TENANT_ID) JOIN (SELECT JOB_NAME, TENANT_ID, MAX(`REVERSION`) AS `REVERSION` FROM ${tablePrefix}job_info GROUP BY JOB_NAME, TENANT_ID) ji_m USING (JOB_NAME, TENANT_ID, `REVERSION` ) WHERE j.MAX_VERSION IS NULL ) job
and lower(JOB_NAME) like concat('%', lower(#{jobName, jdbcType=VARCHAR}), '%')
and lower(DISPLAY_NAME) like concat('%', lower(#{displayName, jdbcType=VARCHAR}), '%')
and `STATUS` in
#{item, jdbcType=INTEGER}
and TENANT_ID = #{tenantId, jdbcType=VARCHAR}
order by JOB_NAME
order by ${sort}

mapper.delete:
delete from ${tablePrefix}job


        JOB_NAME in

            #{jobName}



        and TENANT_ID = #{tenantId, jdbcType=VARCHAR}
