mapper.select:
SELECT COUNT(*) FROM ${tablePrefix}job_instance j






                AND STATUS = #{status, jdbcType=INTEGER}


                AND STATUS in

                    #{item, jdbcType=INTEGER}

SELECT * FROM (
SELECT ji.ID, ji.PROC_INST_ID, ji.ROOT_PROC_INST_ID, ji.JOB_NAME, ji.DISPLAY_NAME, ji.TRIGGER_NAME,
ji.PROC_DEF_ID, ji.STATUS, ji.CODE, ji.MSG, ji.PARENT_ID, ji.RETRIED, ji.SCHEDULER_URI, ji.EXECUTOR_URI,
ji.TENANT_ID, IFNULL(hp.START_TIME_, ji.START_TIME) as START_TIME,
IFNULL(hp.END_TIME_, ji.END_TIME) as END_TIME,
IFNULL(hp.DURATION_, ji.DURATION) as DURATION
FROM (select * from ${tablePrefix}job_instance UNION ALL select * from ${tablePrefix}job_instance_history) ji
LEFT JOIN act_hi_procinst hp on ji.PROC_INST_ID = hp.PROC_INST_ID_) j


        and lower(JOB_NAME) like concat('%', lower(#{jobName, jdbcType=VARCHAR}), '%')


        and lower(DISPLAY_NAME) like concat('%', lower(#{displayName, jdbcType=VARCHAR}), '%')


        and STATUS in

            #{item, jdbcType=INTEGER}



        and START_TIME >= #{lowerStartTime, jdbcType=VARCHAR}


        and START_TIME <= #{upperStartTime, jdbcType=VARCHAR}


        and END_TIME > #{lowerEndTime, jdbcType=VARCHAR}


        and END_TIME <= #{upperEndTime, jdbcType=VARCHAR}






        order by START_TIME DESC


        order by ${sort}
