mapper.select:
SELECT COUNT(*) FROM ${tablePrefix}job_execution j






                AND STATUS = #{status, jdbcType=INTEGER}


                AND STATUS in

                    #{item, jdbcType=INTEGER}

mapper.update:
UPDATE ${tablePrefix}job_instance j

;
DELETE FROM j USING ${tablePrefix}job_execution j

mapper.select:
SELECT j.ID, j.PROC_INST_ID, j.ROOT_PROC_INST_ID, j.JOB_NAME, j.DISPLAY_NAME, j.TRIGGER_NAME,
j.PROC_DEF_ID, j.STATUS, j.CODE, j.MSG, j.PARENT_ID, j.SCHEDULER_URI, j.EXECUTOR_URI,
j.TENANT_ID, j.START_TIME, j.END_TIME, j.DURATION
FROM ${tablePrefix}job_instance j

    AND j.SUPER_PROC_INST_ID IS NULL

        PARENT_ID = ID


        and lower(JOB_NAME) like concat('%', lower(#{jobName, jdbcType=VARCHAR}), '%')


        and lower(DISPLAY_NAME) like concat('%', lower(#{displayName, jdbcType=VARCHAR}), '%')


        and STATUS in

            #{item, jdbcType=INTEGER}



        and START_TIME >= #{lowerStartTime, jdbcType=VARCHAR}


        and START_TIME <= #{upperStartTime, jdbcType=VARCHAR}


        and END_TIME > #{lowerEndTime, jdbcType=VARCHAR}


        and END_TIME <= #{upperEndTime, jdbcType=VARCHAR}






        order by id DESC


        order by ${sort}
