mapper.update:
UPDATE ${tablePrefix}job j SET
`DISPLAY_NAME` = #{displayName, jdbcType=VARCHAR},
JOB_TYPE = #{jobType, jdbcType=INTEGER}, JOB_CONTENT = #{jobContent, jdbcType=VARCHAR}, STATUS = #{status, jdbcType=INTEGER}, CATEGORY_NAME = #{categoryName, jdbcType=VARCHAR}, UPDATE_TIME = #{updateTime, jdbcType=TIMESTAMP}, REMARK = #{remark, jdbcType=VARCHAR}

mapper.select:
select *
from ${tablePrefix}job j


        and JOB_NAME like concat('%', #{jobName, jdbcType=VARCHAR}, '%')


        and `DISPLAY_NAME` like concat('%', #{displayName, jdbcType=VARCHAR}, '%')


        and JOB_TYPE = #{jobType, jdbcType=INTEGER}


        and `STATUS` = #{status, jdbcType=INTEGER}


        and `JOB_TYPE` >= #{lowerJobType, jdbcType=INTEGER}





        order by JOB_NAME


        order by ${sort}

mapper.delete:
delete from ${tablePrefix}job


        JOB_NAME in

            #{jobName}



        and TENANT_ID = #{tenantId, jdbcType=VARCHAR}
